/**
 * A Project
 * @namespace forge.db.models.docker.DockerProject
 */
const { DataTypes, Model } = require('sequelize');

const M = {}

module.exports = function(db) {

    var model = db.sequelize.define('K8SProject',{
        id: { type: DataTypes.UUID, primaryKey: true},
        url: { type: DataTypes.STRING, allowNull: false},
        state: { type: DataTypes.STRING, allowNull: false },
        env: {type: DataTypes.STRING, allowNull: false, defaultValue: '{}'}
    },{
    })

    let finders = function(M) {
        return { static: {
            byId: async (id) => {
                return this.findOne({
                    where: {
                        id: id
                    }
                })
            }
        }}
    }

    let stats = finders.call(model, M)

    Object.assign(model, stats.static)

    db.models.LocalFSProject = model

    model.sync({ alter: true })
}
